import React, { useState, useEffect } from 'react';
import axios from 'axios';
import io from 'socket.io-client';

const socket = io('http://localhost:5000');

const Auction = ({ username }) => {
  const [auction, setAuction] = useState(null);
  const [bidAmount, setBidAmount] = useState(0);

  useEffect(() => {
    axios.get('http://localhost:5000/api/auction').then((response) => {
      setAuction(response.data);
    });

    socket.on('newBid', (auction) => {
      setAuction(auction);
    });
  }, []);

  const handleBid = () => {
    socket.emit('bid', { auctionId: auction._id, username, amount: bidAmount });
  };

  return (
    <div>
      {auction ? (
        <div>
          <img src={auction.picture} alt="Auction Item" />
          <h3>Current Bids</h3>
          <ul>
            {auction.bids.map((bid, index) => (
              <li key={index}>{bid.username}: ${bid.amount}</li>
            ))}
          </ul>
          <input
            type="number"
            value={bidAmount}
            onChange={(e) => setBidAmount(e.target.value)}
            placeholder="Enter your bid"
          />
          <button onClick={handleBid}>Place Bid</button>
        </div>
      ) : (
        <p>Loading auction...</p>
      )}
    </div>
  );
};

export default Auction;
