import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './UserRegistration.css';

const UserRegistration = ({ setUsername }) => {
  const [name, setName] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setUsername(name);
    try {
      const response = await axios.get('http://localhost:5000/api/auction');
      if (response.data) {
        navigate('/auction');
      } else {
        await axios.post('http://localhost:5000/api/auction');
        navigate('/auction');
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="registration-container">
      <h1>Enter the Auction</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Enter your username"
          required
        />
        <button type="submit">Join Auction</button>
      </form>
    </div>
  );
};

export default UserRegistration;
