import React, { useState, useEffect } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import GifSearch from './GifSearch';
import './Auction.css';

const socket = io('http://localhost:5000');

const Auction = ({ username }) => {
  const [auction, setAuction] = useState(null);
  const [bidAmount, setBidAmount] = useState(0);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [gifSearchVisible, setGifSearchVisible] = useState(false);

  useEffect(() => {
    axios.get('http://localhost:5000/api/auction').then((response) => {
      setAuction(response.data);
    });

    socket.on('newBid', (auction) => {
      setAuction(auction);
    });

    socket.on('message', (message) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    return () => {
      socket.off('newBid');
      socket.off('message');
    };
  }, []);

  const handleBid = () => {
    if (bidAmount <= 0 || (auction && bidAmount <= auction.bids[0]?.amount)) {
      alert('Bid amount must be higher than the current highest bid.');
      return;
    }
    socket.emit('bid', { auctionId: auction._id, username, amount: bidAmount });
  };

  const handleSendMessage = () => {
    if (message.trim() === '') return;
    socket.emit('message', { username, text: message });
    setMessage('');
  };

  const handleGifSelect = (gifUrl) => {
    socket.emit('message', { username, text: gifUrl, isGif: true });
    setGifSearchVisible(false);
  };

  return (
    <div className="auction-container">
      {auction ? (
        <div>
          <div className="auction-header">
            <h2>{auction.picture}</h2>
            <h3>Time Left: {auction.timeLeft}</h3>
          </div>
          <div className="auction-content">
            <img src={auction.picture} alt="Auction Item" className="auction-image" />
            <h3>Current Bids</h3>
            <ul>
              {auction.bids.map((bid, index) => (
                <li key={index}>{bid.username}: ${bid.amount}</li>
              ))}
            </ul>
            <input
              type="number"
              value={bidAmount}
              onChange={(e) => setBidAmount(e.target.value)}
              placeholder="Enter your bid"
            />
            <button onClick={handleBid}>Place Bid</button>
          </div>
        </div>
      ) : (
        <p>Loading auction...</p>
      )}

      <div className="chat-container">
        <div className="messages">
          {messages.map((msg, index) => (
            <div key={index} className="message">
              {msg.isGif ? (
                <img src={msg.text} alt="GIF" className="message-gif" />
              ) : (
                <span>{msg.username}: {msg.text}</span>
              )}
            </div>
          ))}
        </div>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type a message"
        />
        <button onClick={handleSendMessage}>Send</button>
        <button onClick={() => setGifSearchVisible(!gifSearchVisible)}>GIF</button>
      </div>

      {gifSearchVisible && <GifSearch onGifSelect={handleGifSelect} />}
    </div>
  );
};

export default Auction;
